    </nav>

    <?php // --- Modal de Status do Formul√°rio --- ?>
    <div id="statusModal" class="modal-overlay <?php echo $form_status ? 'visible' : ''; ?>">
        <div id="modalContent" class="modal-content <?php echo htmlspecialchars($form_status ?? '', ENT_QUOTES, 'UTF-8'); ?>">
             <h2 id="modalTitle" class="modal-title <?php echo htmlspecialchars($form_status ?? '', ENT_QUOTES, 'UTF-8'); ?>"></h2>
             <p id="modalMessage" class="modal-message"></p>
             <button id="closeModalBtn" class="close-modal-btn">Entendido!</button>
        </div>
    </div>

    <?php // --- JavaScript (Notifica√ß√µes + Modal + UI - Chat JS Removido) --- ?>
    <script>
        const currentUserId_Notif=<?php echo json_encode((int)$id);?>;
        const notificationHandlerUrl='notification_handler_ajax.php'; // Ajuste caminho
        let lastNotificationId=0; <?php if(!empty($initial_notifications)){$lid=0;foreach($initial_notifications as $n){if((int)$n['id']>$lid){$lid=(int)$n['id'];}} echo 'lastNotificationId = '.$lid.';';} ?>
        const notificationPollInterval=10000; let notificationPollingIntervalId=null; let isFetchingNotifications=false;
        const notificationListDiv=document.getElementById('notification-list');

        function escapeHtml(unsafe){if(typeof unsafe!=='string'){if(typeof unsafe==='number'){unsafe=String(unsafe);}else{return'';}} return unsafe.replace(/&/g,"&amp;").replace(/</g,"&lt;").replace(/>/g,"&gt;").replace(/"/g,"&quot;").replace(/'/g,"&#039;");}
        function formatTimestamp(timestamp){if(!timestamp)return'??:??'; try{const d=new Date(timestamp.replace(/-/g,'/')+' UTC'); if(!isNaN(d)){return d.toLocaleTimeString('pt-BR',{hour:'2-digit',minute:'2-digit',timeZone:'America/Sao_Paulo'});}}catch(e){console.error("Erro timestamp:", e);} const parts=timestamp.split(' '); if(parts.length>1&&parts[1].includes(':')){return parts[1].substring(0,5);} return'??:??';}
        function initializeNotificationSystem(){ if(!notificationListDiv) {console.warn("Div 'notification-list' n√£o encontrada."); return;} const initialItems = notificationListDiv.querySelectorAll('.notification-item'); initialItems.forEach(item => processNotificationItem(item)); if(notificationPollingIntervalId) clearInterval(notificationPollingIntervalId); setTimeout(fetchNotificationUpdates, 700); notificationPollingIntervalId = setInterval(fetchNotificationUpdates, notificationPollInterval); document.addEventListener("visibilitychange", handleVisibilityChangeNotifications); if(notificationListDiv) { notificationListDiv.scrollTop = 0; } }
        function processNotificationItem(item) { if (!item || item.querySelector('.notification-actions')) return; try { const relatedDataStr = item.dataset.related; const notificationId = item.dataset.notificationId; const isInvite = item.classList.contains('type-invite'); if (isInvite && relatedDataStr && notificationId) { const relatedData = JSON.parse(relatedDataStr); const actionsHtml = generateNotificationActions({ id: notificationId, type: 'invite', related_data: relatedData }); if (actionsHtml) { const actionsContainer = document.createElement('div'); actionsContainer.classList.add('notification-actions'); actionsContainer.innerHTML = actionsHtml; item.appendChild(actionsContainer); } } } catch (e) { console.error("Erro processar notif item:", e, item.dataset.related); } }
        function handleVisibilityChangeNotifications(){ if(document.hidden){ if(notificationPollingIntervalId) clearInterval(notificationPollingIntervalId); notificationPollingIntervalId = null; } else { if(!notificationPollingIntervalId){ setTimeout(fetchNotificationUpdates, 250); notificationPollingIntervalId = setInterval(fetchNotificationUpdates, notificationPollInterval); } } }
        function addNotificationToDisplay(notif) { if (!notificationListDiv) return; if (!notif || !notif.id) return; if (document.querySelector(`.notification-item[data-notification-id="${notif.id}"]`)) return; const emptyMsg = notificationListDiv.querySelector('.notification-empty'); if (emptyMsg) emptyMsg.remove(); const newItem = document.createElement('div'); newItem.dataset.notificationId = notif.id; let relatedDataJson = '{}'; try { let relatedData = notif.related_data; if (typeof relatedData === 'string') { try { relatedData = JSON.parse(relatedData); } catch (e) { relatedData = {}; } } if (typeof relatedData !== 'object' || relatedData === null) { relatedData = {}; } relatedDataJson = JSON.stringify(relatedData, (key, value) => (typeof value === 'string' ? escapeHtml(value) : value)); } catch (e) { console.error("Erro related_data notif:", e); } newItem.dataset.related = relatedDataJson; newItem.classList.add('notification-item', `type-${escapeHtml(notif.type) || 'info'}`); let timestampFormatted = formatTimestamp(notif.timestamp); newItem.innerHTML = `<button class="mark-read-btn" title="Remover Notifica√ß√£o" onclick="markNotificationAsRead(${notif.id}, this)">√ó</button><span class="timestamp">${timestampFormatted}</span><span class="msg-text">${escapeHtml(notif.message_text)}</span>`; processNotificationItem(newItem); notificationListDiv.insertBefore(newItem, notificationListDiv.firstChild); const maxNotifications = 50; while (notificationListDiv.children.length > maxNotifications) { notificationListDiv.removeChild(notificationListDiv.lastChild); } if (notif.id && parseInt(notif.id) > lastNotificationId) { lastNotificationId = parseInt(notif.id); } }
        function generateNotificationActions(notif) { let relatedData = notif.related_data; if (!relatedData || typeof relatedData !== 'object') return ''; if (notif.type === 'invite') { try { if (relatedData.invite_type === 'fusion' && notif.id) { const acceptAction = `handleInviteAction(${notif.id}, 'accept', this)`; const rejectAction = `handleInviteAction(${notif.id}, 'reject', this)`; return `<button onclick="${acceptAction}" class='btn-action btn-aceitar'>Aceitar</button><button onclick="${rejectAction}" class='btn-action btn-recusar'>Recusar</button>`; } } catch (e) { console.error("Erro gerar a√ß√µes notif:", e); } } return ''; }
        async function handleInviteAction(notificationId, action, buttonElement) { if (!notificationId || !action || !buttonElement) return; const actionsContainer = buttonElement.closest('.notification-actions'); if (actionsContainer) { actionsContainer.querySelectorAll('.btn-action').forEach(btn => btn.disabled = true); actionsContainer.style.opacity = '0.5'; } try { const formData = new FormData(); formData.append('notification_id', notificationId); formData.append('invite_action', action); const response = await fetch(`${notificationHandlerUrl}?action=handle_invite`, { method: 'POST', body: formData }); if (!response.ok) { throw new Error(`Erro servidor: ${response.status}`); } const data = await response.json(); if (data.success) { const notificationItem = buttonElement.closest('.notification-item'); if (notificationItem) { notificationItem.style.transition = 'opacity 0.3s, transform 0.3s'; notificationItem.style.opacity = '0'; notificationItem.style.transform = 'translateX(50px)'; setTimeout(() => { notificationItem.remove(); checkEmptyNotifications(); }, 300); } console.log(`A√ß√£o '${action}' id ${notificationId} OK.`); } else { throw new Error(data.error || 'Falha servidor.'); } } catch (error) { console.error('Erro a√ß√£o convite:', error); alert(`Erro ao ${action === 'accept' ? 'aceitar' : 'recusar'} convite.`); if (actionsContainer) { actionsContainer.querySelectorAll('.btn-action').forEach(btn => btn.disabled = false); actionsContainer.style.opacity = '1'; } } }
        async function markNotificationAsRead(notificationId, buttonElement) { if (!notificationId || !buttonElement) return; const notificationItem = buttonElement.closest('.notification-item'); if (notificationItem) { notificationItem.style.transition = 'opacity 0.3s, transform 0.3s, height 0.3s, margin 0.3s, padding 0.3s'; notificationItem.style.opacity = '0'; notificationItem.style.transform = 'scale(0.9) translateX(30px)'; notificationItem.style.height = '0'; notificationItem.style.marginBottom = '0'; notificationItem.style.paddingTop = '0'; notificationItem.style.paddingBottom = '0'; notificationItem.style.borderWidth = '0'; setTimeout(() => { notificationItem.remove(); checkEmptyNotifications(); }, 300); } try { const formData = new FormData(); formData.append('notification_id', notificationId); const response = await fetch(`${notificationHandlerUrl}?action=mark_notification_read`, { method: 'POST', body: formData }); if (!response.ok) { console.error('Erro backend marcar lida.'); } } catch (error) { console.error('Erro rede marcar lida:', error); } }
        function checkEmptyNotifications() { if (notificationListDiv && notificationListDiv.children.length === 0) { const emptyMsg = document.createElement('p'); emptyMsg.classList.add('notification-empty'); emptyMsg.textContent = 'Nenhuma notifica√ß√£o.'; notificationListDiv.appendChild(emptyMsg); } }
        async function fetchNotificationUpdates() { if (isFetchingNotifications || document.hidden || !notificationListDiv) return; isFetchingNotifications = true; try { const fetchUrl = `${notificationHandlerUrl}?action=fetch&last_notification_id=${lastNotificationId}&t=${Date.now()}`; const response = await fetch(fetchUrl); if (!response.ok) { console.error("Erro buscar notifs:", response.status); isFetchingNotifications = false; return; } const data = await response.json(); if (data.success && data.notifications && data.notifications.length > 0) { data.notifications.reverse().forEach(notif => { addNotificationToDisplay(notif); }); } } catch (error) { console.error("Erro rede buscar notifs:", error); } finally { isFetchingNotifications = false; } }

        // --- Inicializa√ß√£o Geral e L√≥gica UI ---
        document.addEventListener('DOMContentLoaded', () => {
             document.querySelectorAll('.entrar-templo-btn:not(.disabled)').forEach(button => { button.addEventListener('click', event => { event.preventDefault(); const url = button.dataset.url || button.getAttribute('href'); if (url && url !== '#') { window.location.href = url; } else { console.warn("URL Templo inv√°lida.", button); } }); });
             const bottomNav = document.getElementById('bottom-nav'); const rightColumn = document.querySelector('.coluna-direita'); function checkBottomNavVisibility() { if (!bottomNav || !rightColumn) return; if (window.getComputedStyle(rightColumn).display === 'none') { bottomNav.style.display = 'flex'; document.body.style.paddingBottom = (bottomNav.offsetHeight + 10) + 'px'; } else { bottomNav.style.display = 'none'; document.body.style.paddingBottom = '0'; } } checkBottomNavVisibility(); window.addEventListener('resize', checkBottomNavVisibility);

             // --- L√≥gica do Modal de Status (Integrada) ---
            const modalOverlay = document.getElementById('statusModal');
            const modalContent = document.getElementById('modalContent');
            const modalTitle = document.getElementById('modalTitle');
            const modalMessage = document.getElementById('modalMessage');
            const closeModalBtn = document.getElementById('closeModalBtn');
            const formStatus = <?php echo json_encode($form_status); ?>; // Pega status do PHP via sess√£o
            const formUserName = <?php echo json_encode($form_user_name); ?>;

            function closeModal() { if (modalOverlay) modalOverlay.classList.remove('visible'); }

            if(closeModalBtn) { closeModalBtn.addEventListener('click', closeModal); } // Bot√£o s√≥ fecha
            if(modalOverlay){ modalOverlay.addEventListener('click', function(event) { if (event.target === modalOverlay) { closeModal(); } }); }

            if (formStatus && modalTitle && modalMessage && modalContent) {
                let title = ''; let message = ''; let messageType = formStatus;
                modalContent.className = 'modal-content ' + messageType; modalTitle.className = 'modal-title ' + messageType;
                if (formStatus === 'success') { const userNameDecoded = formUserName ? decodeURIComponent(formUserName.replace(/\+/g, ' ')) : 'Guerreiro(a)'; title = 'Miss√£o Cumprida!'; message = `Valeu, ${userNameDecoded}! Seu relat√≥rio foi enviado. Obrigado por colaborar!`; }
                else if (formStatus === 'error') { title = 'Alerta Vermelho!'; message = 'Oops! Houve um erro ao enviar seu relat√≥rio. Tente novamente ou avise um Admin.'; }
                else if (formStatus === 'invalid') { title = 'Dados Incompletos!'; message = 'Aten√ß√£o! Faltam informa√ß√µes no seu relat√≥rio. Preencha todos os campos.'; }
                modalTitle.textContent = title; modalMessage.textContent = message; modalOverlay.classList.add('visible');
                 // Limpa URL se houver par√¢metros status/name (ap√≥s mostrar o modal)
                 if (window.history.replaceState && (formStatus)) {
                     setTimeout(() => {
                        const cleanUrl = window.location.protocol + "//" + window.location.host + window.location.pathname;
                        window.history.replaceState({ path: cleanUrl }, '', cleanUrl);
                     }, 500); // Pequeno delay
                 }
            }
            // --- Fim L√≥gica Modal ---

            initializeNotificationSystem(); // Inicializa notifica√ß√µes
            // Chat n√£o √© mais inicializado aqui
        });
    </script>

</body>
</html>
